package com.syntax.class09;

public class MoreNestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//how to create a multiplication table
		for(int i=1; i<=10; i++) {
			for(int y=1; y<=10; y++) {
				System.out.print(i+"*"+y+"="+(i*y)+"   ");
				
			}
			
		}
		

	}

}
