package com.syntax.class13;

public class StringDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str="Batach 13 is good actually very good";
		System.out.println(str.replace("good", "Great"));
		System.out.println(str.replace("g", "G"));
		System.out.println(str.replace("g", "T"));
		System.out.println(str.replace(str, "Hello I am here"));
		System.out.println(str.replace(" ", ""));
	
		System.out.println(str.replaceFirst("g", "G"));
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		

	}

}
